class Solution: 
    def isPalindrome(self, s):
        strLen = len(s)
        # print(strLen)
        firstHalf = ""
        secondHalf = ""
        half = int(strLen / 2)
        firstHalf = s[:half]
        if (strLen % 2) == 0:
            secondHalf = s[half:]
            secondHalf = secondHalf[::-1]
            # reversed is slower than extended
            # secondHalf = "".join(reversed(secondHalf)) 
    
        else:
            secondHalf = s[half+1:]
            secondHalf = secondHalf[::-1]
            # secondHalf = "".join(reversed(secondHalf)) 

        return firstHalf == secondHalf
        

    def longestPalindrome(self, s):
        if s == "":
            return ""
        if self.isPalindrome(s):
            return s

        maxPalind =""
        strLen = len(s)
        maxPalindLen = 0
        for i in range(0, strLen):
            for j in range (strLen-1  , i-1  , -1):
                # print("i: ", i, " j ", j)
                subString = s[i:j+1]
                # print(subString)
                lenSubstring = len(subString)
                if lenSubstring < maxPalindLen:
                    continue
                if self.isPalindrome(subString) and maxPalindLen < lenSubstring:
                    maxPalind = subString
                    maxPalindLen = len(maxPalind)
                    break
        return maxPalind
            # print(subString)
        
